import React, { useState,useEffect } from "react";
import noteService from './services/services';

const Notification = ({ successMessage,errorMessage }) => {
  if (successMessage===null && errorMessage===null) {
    return null;
  }
  return (
<div className={errorMessage ? "error" : "success"}>{successMessage}{errorMessage}</div>
  )
}

const Filter = ({setSearch,search}) => {
  const handleSearch = ev => {
    search = ev.target.value.trim().toLowerCase();
    setSearch(search)
};
  return (
    <input onChange={handleSearch}  value={search} />
  )
}

const PersonForm = ({notes, setNote, setSuccessMessage}) => {
  const [newName, setNewName] = useState({name:"",number:""});
  const handleForm = ev => {
    console.log(ev)
    ev.preventDefault();
    let id;
    let updated_notes;
    if (notes.some(person => person.name === newName.name)) {
      if(window.confirm(`${newName.name} is already added to phonebook. Replace the old number with a new one?`)) {
        updated_notes = notes.filter((note) => { if (note.name === newName.name) {
          note.number = newName.number;
          id = note.id
        }
        return note;
      })
        noteService.update(id, newName)
        setNote(updated_notes)
        setSuccessMessage(`Number of ${newName.name} has been updated`)
        setTimeout(()=>{setSuccessMessage(null)},5000)
        let emptyName = {
          name: "",
          number: ""
        };
        setNewName(emptyName);
      }
    } else {
      noteService.create(newName)
        notes = notes.concat(newName);
        setNote(notes);
        setSuccessMessage(`Added ${newName.name} successfully to the list`)
        setTimeout(() => {setSuccessMessage(null)},5000)

      let emptyName = {
        name: "",
        number: ""
      };
      setNewName(emptyName);
    }
  };



  const handleInputName = ev => {
    let updatedName = {
      name: ev.target.value
    };
    setNewName(updatedName);
  };

  const handleInputNumber = ev => {
    let updatedName = {
      ...newName,
      number: ev.target.value,
      id : Math.floor((Math.random() * 1000) + 1)
    };
    setNewName(updatedName);
  };
  return (
    <form onSubmit={handleForm}>
    <div>
      name:{" "}
      <input onChange={handleInputName} value={newName.name} name="name" />
      <br />
      number:{" "}
      <input
        onChange={handleInputNumber}
        value={newName.number}
        name="number"
      />
    </div>
    <div>
      <button type="submit">add</button>
    </div>
  </form>
  )
}

const Persons = ({notes, search, setNote,setErrorMessage}) => {
  const deleteNote = (id,name) => {
    if (window.confirm('Are you sure to delete this note?')) {
      noteService.deleteNote(id).then(() =>{
        notes = notes.filter((note) => note.id !== id)
        setNote(notes);
      })
      .catch(()=>{
        setErrorMessage(`Information of ${name} has already been deleted from server`)
        setTimeout(()=>{setErrorMessage(null)},5000)
      })
    }
    else {
      return null
    }
  }
  return (
    <div>{search.length > 0 ? 
      notes.filter(person => {return person.name.toLowerCase().match(search)}).map((v, i) => {
    return <div key={i}>{v.name} {v.number}<button onClick={() => deleteNote(v.id,v.name)}>delete</button></div> }): notes.map((v, i) => {
      return <div key={i}>{v.name} {v.number}<button onClick={() => deleteNote(v.id,v.name)}>delete</button></div> })
    }</div>
  )
}

const App = () => {
  const [notes, setNote] = useState([]);
  const [input, setInput] = useState([]);
  const [successMessage, setSuccessMessage] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);
  const [search, setSearch] = useState("");

  const hook = () => {
      noteService.getAll().then(response => {
        setNote(response.data)
      })
  }

  useEffect(hook,[])

  return (
    <div>
      <Notification successMessage={successMessage} errorMessage={errorMessage}/>
      <h2>phonebook</h2>
      <div>
        filter shown with <Filter search={search} setSearch={setSearch}  />
      </div>
      <h2>add a new</h2>
      <PersonForm notes={notes} setNote={setNote} setSuccessMessage={setSuccessMessage} />
      <h2>Numbers</h2>
      <Persons notes={notes} search={search} setNote={setNote} setErrorMessage={setErrorMessage}/>
    </div>
  );
};

export default App;
