import React, { useState, useEffect } from "react";
import Axios from "axios";

const Filter = ({persons,setPersons,setCity}) => {
  const handleSearch = ev => {
    let searchedpersons = persons.filter((asset) => asset.name.toLowerCase().indexOf(ev.target.value) !== -1);
    if(searchedpersons.length === 1) {
      const Api_Key = '8d2de98e089f1c28e1a22fc19a24ef04';
      Axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${searchedpersons[0].capital}&appid=${Api_Key}&units=metric`).then(resp => {
        console.log(resp.data)
        setCity(resp.data)
      })
    }
    setPersons(searchedpersons);

    if(!ev.target.value) {
      setPersons(persons)
    }
};
  return (
    <input onChange={handleSearch} />
  )
}

const Persons = ({ persons, setPersons,detail,setDetail }) => {
  const handleCountries = () => {
    // let persons =[  {
    //   name : v.name,
    //   capital : v.capital,
    //   population : v.population,
    //   flag : v.flag,
    //   languages : v.languages
    // }]
    // setPersons(persons)
    let detail = true;
    setDetail(detail);
    console.log(detail)
  }

  return (
    <div>{persons.length === 1 ?
      persons.map((v, i) => {
      return <div key={i}><h2>{v.name}</h2> <div>capital {v.capital}</div> <div>population {v.population}</div><h3>languages</h3><ul> {v.languages.map((v,i)=>{
        return <li key={i}>{v.name}</li>
      })}</ul><img width="160px" height="100px" alt="country flag" src={v.flag}/></div>;
    }) :  persons.map((v, i) => {
    return <div key={i}>{v.name} {detail ? <div>fdddddddddddalse</div> : <button onClick={() => handleCountries}>Show more</button>}</div>;
    })
    }
    </div>
  )
}

const App = () => {
  const [persons, setPersons] = useState([]);
  const [city, setCity] = useState([]);
  const [detail,setDetail] = useState(false);

  const hook = () => {
    Axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {

        setPersons(response.data)
        setCity(response.data[0].city)
      })
  }

  useEffect(hook, [])

  return (
    <div>
      <h2>find countries</h2>
      <div>
        filter shown with <Filter persons={persons} setPersons={setPersons} setCity={setCity} />
      </div>
      <h2>Numbers</h2>
       {persons.length >= 10 ? 'Too many matches, specify another filter':
      <div>
      <Persons persons={persons} setPersons={setPersons} detail={detail} setDetail={setDetail}/>
      {persons.length === 1 && city && <div><h2>weather in {city.name}</h2><div>temperature : {city.main.temp} C </div><div>wind : {city.wind.speed} kph</div></div>}
      </div>}
    </div>
  );
};

export default App;
