import React, { useState, useEffect } from "react";
import Axios from "axios";

const Filter = ({persons,setPersons,setCity,setSearch}) => {
  const handleSearch = ev => {
    let search = ev.target.value.trim().toLowerCase();
    console.log(search)
    setSearch(search);
    // let searchedpersons = persons.filter((asset) => asset.name.toLowerCase().indexOf(ev.target.value) !== -1);
    // if(searchedpersons.length === 1) {
    //   const Api_Key = '8d2de98e089f1c28e1a22fc19a24ef04';
    //   Axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${searchedpersons[0].capital}&appid=${Api_Key}&units=metric`).then(resp => {
    //     console.log(resp.data)
    //     setCity(resp.data)
    //   })
    // }x
    // setPersons(searchedpersons);
};
  return (
    <input onChange={handleSearch} />
  )
}

const Persons = ({ persons, setPersons,detail,setDetail,search }) => {

  const handleCountries = (v) => {
    // persons = persons.map((list) => { 
    //  if(list.name === v.name) {
    //    return list
    //  }
    //  else return { name : list.name};
    // })
    // setPersons(persons)
    // let detail = true;
    // setDetail(detail);
  }


  return (

    <div>{ search.length > 0 ? persons.filter((person) => { 
      return person.name.trim().toLowerCase().match(search)
    }).map((v, i) =>  {
      console.log(v.name)
      // console.log(v.name.startsWith(search.split(/\s+/).slice(0,3).join(" ")))
    // return <div key={i}>{v.name.split(/\s+/).slice(0,4).join(" ") == search.split(/\s+/).slice(0,4).join(" ") && i === 0 ? (
    //   <div><h1>{v.name}</h1><div>{v.capital}</div><div>{v.population}</div>
    //   <img src={v.flag} width="150px" height="100px"/>
    //   </div>
    // ) : (
    //   <div>{v.name}</div>
    // )}
    // </div>
  }
    )
    : persons.map((v, i) => {
      return <div key={i}>{v.name}{detail && <div>
        <div>{v.capital}</div><div>{v.population}</div>
        {v.flag && <img width="160px" height="100px" alt="country flag" src={v.flag}/>}</div>} 
      </div>;
      })}
    </div>
  )
}

const App = () => {
  const [persons, setPersons] = useState([]);
  const [city, setCity] = useState([]);
  const [detail,setDetail] = useState(false);
  const [search, setSearch] = useState("");

  const hook = () => {
    Axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log(response.data)
        setPersons(response.data)
        // setCity(response.data[0].city)
      })
  }

  useEffect(hook, [])

  return (
    <div>
      <h2>find countries</h2>
      <div>
        filter shown with <Filter persons={persons} setSearch={setSearch} setPersons={setPersons} setCity={setCity} />
      </div>
      <h2>Numbers</h2>
       {/* {persons.length >= 10 ? 'Too many matches, specify another filter ': */}
      <div>
      <Persons persons={persons} setPersons={setPersons} search={search} detail={detail} setDetail={setDetail}/>
      {persons.length === 1 && city && <div><h2>weather in {city.name}</h2><div>temperature : {city.main.temp} C </div><div>wind : {city.wind.speed} kph</div></div>}
      </div>
    </div>
  );
};

export default App;
